import React, { useState, useEffect } from 'react';

// import { getPostsByStarWars } from '../services/starWarsAPI';

function Provider() {
  const STARWARS_BASE_API = 'https://swapi-trybe.herokuapp.com/api/planets/';
  const [data, setData] = useState([]);

  const getPostsByStarWars = () => {
    fetch(`${STARWARS_BASE_API}`)
      .then((res) => res.json())
      .then((json) => setData(json));
  };
  useEffect(() => {
    getPostsByStarWars();
  }, []);
  return (
    <div className="container">
      <h1>Simple Inventory Table</h1>
      <table>
        <thead>
          <tr>
            <th>Product Name</th>
            <th>Product Category</th>
            <th>Unit Price</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {
            data.results.name.map((item) => (
              <tr key={ item.name }>
                <td>{item.name}</td>
                <td>{item.name}</td>
                <td>{item.name}</td>
                <td />
              </tr>
            ))
          }
        </tbody>
      </table>
    </div>

  );
}

export default Provider;
